import styled from '@emotion/styled';

export const NavBar = styled.ul`
  display: flex;
  list-style: none;
  gap: 100px;
`;

export const NavItem = styled.span`
  text-decoration: none;
  border: none;
  color: white;
`;

export const Header = styled.header`
  top: 0;
  left: 0;
  position: sticky;
  z-index: 1100;
  display: flex;
  min-height: 30px;
  padding-right: 24px;
  padding-left: 24px;
  padding-top: 10px;
  padding-bottom: 10px;
  color: #fff;
  background: rgb(113, 111, 129);
  background: linear-gradient(
    90deg,
    rgba(113, 111, 129, 1) 0%,
    rgba(185, 122, 149, 1) 25%,
    rgba(246, 174, 153, 1) 60%,
    rgba(242, 225, 193, 1) 99%
  );
  box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2),
    0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
`;
