import styled from '@emotion/styled';

export const NavigBar = styled.ul`
  display: flex;
  list-style: none;
  gap: 100px;
`;

export const NavItem = styled.span`
  text-decoration: none;
  border: none;
  color: white;
  font-size: 25px;
  font-weight: 300;
  letter-spacing: 0.08em;

  &.active {
    color: red;
  }
`;
