import { CastItem, CastPhoto, Name, Character } from './CastCard.styled';

const CastCard = ({ role, name, profile }) => {
  const photo = `https://image.tmdb.org/t/p/w500/${profile}`;
  return (
    <CastItem>
      <CastPhoto src={photo} alt={name} />
      <Name>{name}</Name>
      <p>Character: {role}</p>
    </CastItem>
  );
};

export default CastCard;
