import styled from '@emotion/styled';

export const NavBar = styled.ul`
  display: flex;
  list-style: none;
  gap: 100px;
`;

export const NavItem = styled.span`
  text-decoration: none;
  border: none;
  color: white;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 0.08em;
`;
